import React, { useState, useEffect } from "react";
import Axios from "axios";
import FlashcardList from "./FlashcardList";
import "./App.css";

const App = () => {
  const [flashcards, setFlashCard] = useState([]);

  useEffect(() => {
    Axios.get("https://opentdb.com/api.php?amount=15").then((response) => {
      response.data.results.map((flashcard, index) => {
        const answer = flashcard.correct_answer;
        const options = [...flashcard.incorrect_answers, answer];
        return {
          id: `${index}- ${Date.now()}`,
          question: flashcard.question,
          answer: answer,
          options: options.sort(() => Math.random() - 0.5),
        };
      });
    });
  }, []);

  return (
    <div className="App">
      <h1>Trivia</h1>
      <FlashcardList flashcard={flashcards} />
    </div>
  );
};

export default App;
