import React, { useState, useEffect } from "react";
import Axios from "axios";
import FlashcardList from "./FlashcardList";
import "./App.css";

const App = () => {
  const [flashcards, setFlashCard] = useState([]);

  const getQuestions = async () => {
    const response = await Axios.get("https://opentdb.com/api.php?amount=15");
    console.log(response.data);
    setFlashCard(response.data.results);
  };
  const renderTrivia = () => {
    return flashcards.map((flashcard) => {
      return (
        <ul>
          <li>{flashcard.category}</li>
          <li dangerouslySetInnerHTML={{ __html: flashcard.question }}></li>
          <li>A:{flashcard.incorrect_answers[0]}</li>{" "}
          <li>B:{flashcard.incorrect_answers[1]}</li>
          <li>C:{flashcard.correct_answer}</li>
          <li>D:{flashcard.incorrect_answers[2]}</li>
        </ul>
      );
    });
  };

  useEffect(() => {
    getQuestions();
  }, []);
  return (
    <div className="App">
      <h1>Trivia</h1>
      {renderTrivia()}
      <FlashcardList flashcard={flashcards} />
    </div>
  );
};

export default App;
